import axios from "axios";
import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Keyboard, ScrollView, Image } from "react-native";
import { Picker } from '@react-native-picker/picker';
import { StatusBar } from "expo-status-bar";
import { SafeAreaView } from "react-native-safe-area-context";

export default function CadastrarPets() {
  const [protetor, setProtetor] = useState("");
  const [tipo, setTipo] = useState("");
  const [nome, setNome] = useState("");
  const [porte, setPorte] = useState("");
  const [raca, setRaca] = useState("");
  const [idade, setIdade] = useState("");


  async function cadastrarDados() {
    try {
      const res = await axios.get(
        //onde está "192.168.100.93" cooque seu ipv4
        "http://192.168.100.93/api_sgpApp/cadastrar_pet.php?protetor=" +
        protetor +
        "&tipo_pet=" +
        tipo +
        "&nome=" +
        nome +
        "&porte=" +
        porte +
        "&raca=" +
        raca +
        "&idade=" +
        idade
      );
      Keyboard.dismiss();
      alert("Cadastro realizado");
    } catch (error) {
      console.log("Erro: ", error);
    }

  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView >
        <StatusBar hidden={true}></StatusBar>

        <Text style={styles.titulo}>Cadastrar Pets</Text>


        <View style={styles.areaCad}>
          <View style={{ alignItems: "center" }}>
            <Text style={styles.text}>Digite o nome do pet</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite o nome do pet"
              value={nome}
              onChangeText={(texto) => setNome(texto)}
            />
          </View>

          <View style={{ alignItems: "center" }}>
            <Text style={styles.text}>Digite o id do Protetor</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite o nome do Protetor"
              value={protetor}
              onChangeText={(texto) => setProtetor(texto)}
            />
          </View>

          <View style={{ alignItems: "center" }}>
            <Text style={styles.text}>Digite o tipo do animal</Text>

            <TouchableOpacity style={styles.picker}>
              <Picker
                selectedValue={tipo}
                onValueChange={(itemValue, itemIndex) => setTipo(itemValue)}
              >
                <Picker.Item label="Selecione o tipo" value="" />
                <Picker.Item label="Gato" value="1" />
                <Picker.Item label="Cachorro" value="2" />
              </Picker>
            </TouchableOpacity>

          </View>

          <View style={{ alignItems: "center" }}>
            <Text style={styles.text}>Digite a raça do pet</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite a raça do pet"
              value={raca}
              onChangeText={(texto) => setRaca(texto)}
            />
          </View>

          <View style={{ alignItems: "center" }}>
            <Text style={styles.text}>Digite o porte do pet</Text>

            <TouchableOpacity style={styles.picker}>
              <Picker
                selectedValue={porte}
                onValueChange={(itemValue, itemIndex) => setPorte(itemValue)}
              >
                <Picker.Item label="Selecione o porte" value="" />
                <Picker.Item label="Pequeno" value="Pequeno" />
                <Picker.Item label="Médio" value="Médio" />
                <Picker.Item label="Grande" value="Grande" />
              </Picker>
            </TouchableOpacity>
          </View>

          <View style={{ alignItems: "center", marginBottom: 30 }}>
            <Text style={styles.text}>Digite a idade do pet</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite a idade do pet"
              value={idade}
              onChangeText={(texto) => setIdade(texto)}
            />
          </View>
        </View>

        <View style={styles.areaBtn}>
          <TouchableOpacity
            style={[styles.botao]}
            onPress={cadastrarDados}
          >
            <Text style={styles.botaoText}>Cadastrar</Text>
          </TouchableOpacity>
        </View>

      </ScrollView>

      <View style={styles.tabNav}>

        <View style={{ backgroundColor: '#000', height: '80%', width: 2, borderRadius: 10, marginLeft: '2%' }} />

        <View style={styles.areaBtnTab}>
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={() => navigation.navigate('Gerenciar Protetores')}
          >
            <Image
              source={require('../../assets/user.jpg')}
              style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Protetetores</Text>
          </TouchableOpacity>
        </View>

        <View style={{ backgroundColor: '#000', height: '80%', width: 2, borderRadius: 10 }} />

        <View style={styles.areaBtnTab}>
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={() => navigation.navigate('Home')}
          >
            <Image
              source={require('../../assets/img_Home.png')}
              style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Home</Text>
          </TouchableOpacity>
        </View>

        <View style={{ backgroundColor: '#000', height: '80%', width: 2, borderRadius: 10 }} />

        <View style={styles.areaBtnTab}>
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={() => navigation.navigate('Gerenciar Doações')}
          >
            <Image
              source={require('../../assets/doacao.png')}
              style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Doações</Text>
          </TouchableOpacity>
        </View>

        <View style={{ backgroundColor: '#000', height: '80%', width: 2, borderRadius: 10, marginRight: '2%' }} />

      </View>

    </SafeAreaView>


  );
}

//css
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  titulo: {
    backgroundColor: "#4F7063",
    fontSize: 50,
    height: 80,
    textAlignVertical: 'center',
  },
  areaCad: {
    borderRadius: 20,
    marginRight: 10,
    marginLeft: 10,
    marginTop: 30,
    marginBottom: 15,
    backgroundColor: "#4F7063",
  },
  text: {
    marginTop: 25,
    marginBottom: 15,
    fontSize: 25,
    fontWeight: "bold",
  },
  input: {
    backgroundColor: "#FFF",
    borderWidth: 1,
    borderColor: "#DDD",
    borderRadius: 5,
    width: "90%",
    padding: 10,
    fontSize: 18,
  },
  areaBtn: {
    alignItems: "center",
    flexDirection: "row",
    marginBottom: 15,
    justifyContent: "center",
  },
  botao: {
    backgroundColor: "#1d75cd",
    height: 55,
    justifyContent: "center",
    alignItems: "center",
    padding: 15,
    borderRadius: 5,
  },
  botaoText: {
    fontSize: 22,
    color: "#FFF",
  },
  resultado: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  itemText: {
    fontSize: 22,
  },
  picker: {
    backgroundColor: "#FFF",
    borderWidth: 1,
    borderColor: "#DDD",
    borderRadius: 5,
    width: "90%",
    height: 50,
  },
  tabNav: {
    flexDirection: "row",
    justifyContent: 'space-between',
    alignItems: "center",
    backgroundColor: "#4F7063",
    height: '11%',
    width: '100%',
  },
  areaBtnTab: {
    width: '23%',
    height: '10%',
    justifyContent: 'center',
    alignItems: "center",
  },
  botaoTab: {
    justifyContent: 'center',
    alignItems: "center",
  },
  imageTab: {
    width: 40,
    height: 40,
  },
  botaoTextTab: {
    fontSize: 17,
    color: "#000",
  },
});
