import React from "react";
import { View, Text, StyleSheet, TouchableOpacity} from "react-native";
import Feather from "react-native-vector-icons/Feather";


export default function ListarPets({ data, deleteItem, editItem}) {
    return (
        <View style={styles.container}>
            <Text style={styles.text}>ID: {data.id}</Text>
            <Text style={styles.text}>Doador: {data.doador}</Text>
            <Text style={styles.text}>Tipo: {data.tipo}</Text>
            <Text style={styles.text}>Descrição: {data.descricao}</Text>
            <Text style={styles.text}>Quantia: {data.quantia}</Text>

            <TouchableOpacity style={{marginRight:10}} onPress={()=> deleteItem(data.id)}>
                <Feather name="trash" color="#fff" size={20}/>
            </TouchableOpacity>

            <TouchableOpacity style={{marginRight:10}} onPress={()=> editItem(data.id)}>
                <Feather name="edit" color="#fff" size={20}/>
            </TouchableOpacity>

        </View>


    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1, marginTop: 10,
        marginBottom: 5,
        padding: 10,
        backgroundColor: '#121212'


    },
    text: {
        color: '#FFF', fontSize: 17

    }
});
