import axios from "axios";
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
    View, Text, TextInput, TouchableOpacity, StyleSheet,
    SafeAreaView, Keyboard, FlatList
} from "react-native";
import Listagem from "../func/Listagem_doacao";
export default function Listar_doacoes() {
    const [lista, setLista] = useState([]);

    //quando a tela for carregada ele chama essa função
    useEffect(() => {
        listarDados();
    }, []);
    async function listarDados() {
        try {
            //onde está "192.168.100.93" cooque seu ipv4
            const res = await axios.get("http://192.168.100.93/api_sgpApp/listar_doacoes.php");
            setLista(res.data.result);
            Keyboard.dismiss();
        } catch (error) {
            console.log('Erro: ', error);
        }
    }
    return (
        <SafeAreaView style={styles.container}>

            <View style={styles.titulo}>

            <Text style={styles.txtTitulo}>Visualizar Doações</Text>

            </View>

            <FlatList
                keyExtractor={item => item.id}
                data={lista}
                renderItem={({ item }) => (<Listagem data={item} />)}
            />
        </SafeAreaView>
    );
}
//css
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "red",
        justifyContent: 'flex-start',
    },
    titulo:{
        justifyContent: 'center',
        height: '20%',
        width: '100%',
    },
    txtTitulo: {
        backgroundColor: "#4F7063",
        fontSize: 50,
    },
});
