import axios from "axios";
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet, SafeAreaView, Keyboard, FlatList, } from "react-native";

export default function CadastraProtetor() {
    const [telefone, setTelefone] = useState("");
    const [cargo, setCargo] = useState("");
    const [nome, setNome] = useState("");
    const [sobrenome, setSobrenome] = useState("");
    const [rua, setRua] = useState("");
    const [numero, setNumero] = useState("");
    const [bairro, setBairro] = useState("");
    const [cidade, setCidade] = useState("");
    const [uf, setUf] = useState("");
    const [data_nasc, setDataNasc] = useState("");
    const [user, setUser] = useState("");
    const [senha, setSenha] = useState("");

    function limpar() {
        setTelefone('');
        setCargo('');
        setNome('');
        setSobrenome('');
        setRua('');
        setNumero('');
        setBairro('');
        setCidade('');
        setUf('');
        setDataNasc('');
        setUser('');
        setSenha('');
    }
    async function cadastrarDados() {
        try {
            const res = await axios.get(
                "http://localhost/api_sgpApp/cadastrar_protetor.php?telefone=" +
                telefone +
                "&cargo=" +
                cargo +
                "&nome=" +
                nome +
                "&sobrenome=" +
                sobrenome +
                "&rua=" +
                rua +
                "&numero=" +
                numero +
                "&bairro=" +
                bairro +
                "&cidade=" +
                cidade +
                "&uf=" +
                uf +
                "&data_nasc=" +
                data_nasc +
                "&user=" +
                user +
                "&senha=" +
                senha
            );
            console.log(res.data.result);
            setLista(res.data.result);
            Keyboard.dismiss();
            alert("Cadastro realizado");
            limpar();
            listarDados();
        } catch (error) {
            console.log("Erro: ", error);
        }
    }

    return (
        <SafeAreaView style={styles.container}>

        <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite o id do telefone</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o id"
          value={protetor}
          onChangeText={(texto) => setTelefone(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite o Id do cargo</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o ID"
          value={tipo}
          onChangeText={(texto) => setCargo(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite o nome do protetor</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o nome do protetor"
          value={nome}
          onChangeText={(texto) => setNome(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite o sobrenome do protetor</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o sobrenome"
          value={porte}
          onChangeText={(texto) => setSobrenome(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite a rua do protetor</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a rua"
          value={raca}
          onChangeText={(texto) => setRua(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite o bairro</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o bairro"
          value={idade}
          onChangeText={(texto) => setBairro(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite a cidade</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a cidade"
          value={idade}
          onChangeText={(texto) => setCidade(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite a UF</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a UF"
          value={idade}
          onChangeText={(texto) => setUf(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite a data de nascimento</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a data de nascimento"
          value={idade}
          onChangeText={(texto) => setDataNasc(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite nome de usuario</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite o nome de usuario"
          value={idade}
          onChangeText={(texto) => setUser(texto)}
        />
      </View>

      <View style={{ alignItems: "center" }}>
        <Text style={styles.text}>Digite a senha provisoria do usuario</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a senha"
          value={idade}
          onChangeText={(texto) => setSenha(texto)}
        />
      </View>

      <View style={styles.areaBtn}>
        <TouchableOpacity
          style={[styles.botao, { backgroundColor: "#1d75cd" }]}
          onPress={cadastrarDados}
        >
          <Text style={styles.botaoText}>Cadastrar</Text>
        </TouchableOpacity>
      </View>
        </SafeAreaView>
    );
}

//css
const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    text: {
        marginTop: 25,
        marginBottom: 15,
        fontSize: 25,
        fontWeight: "bold",
    },
    input: {
        backgroundColor: "#FFF",
        borderWidth: 1,
        borderColor: "#DDD",
        borderRadius: 5,
        width: "90%",
        padding: 10,
        fontSize: 18,
    },
    areaBtn: {
        alignItems: "center",
        flexDirection: "row",
        marginTop: 15,
        justifyContent: "space-around",
    },
    botao: {
        height: 50,
        justifyContent: "center",
        alignItems: "center",
        padding: 15,
        borderRadius: 5,
    },
    botaoText: {
        fontSize: 22,
        color: "#FFF",
    },
    resultado: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    itemText: {
        fontSize: 22,
    },
});
