import { View, Text, StyleSheet, TouchableOpacity} from "react-native";


export default function Listagem_pets({ data, deleteItem, navigation}) {

    return (
        <View>
            <View style={styles.container}>
                <Text style={styles.text}>Protetor: {data.protetor}</Text>
                <Text style={styles.text}>Tipo: {data.tipo}</Text>
                <Text style={styles.text}>Nome: {data.nome}</Text>
                <Text style={styles.text}>Porte: {data.porte}</Text>
                <Text style={styles.text}>Ra√ßa: {data.raca}</Text>
                <Text style={styles.text}>Idade: {data.idade}</Text>
            </View>

            <View style={styles.areaBtn}>
                <TouchableOpacity 
                style={[styles.botao, { backgroundColor: "#4F7063" }]} 
                onPress={() => deleteItem(data.id)}>
                    <Text style={styles.botaoText}>Deletar</Text>
                </TouchableOpacity>

                <TouchableOpacity
                    style={[styles.botao, { backgroundColor: "#4F7063" }]}
                    onPress={() => editItem()}
                >
                    <Text style={styles.botaoText}>Editar</Text>
                </TouchableOpacity>
            </View>

            <View style={{backgroundColor:"#fff", width:"80%", height:"1%"}}/>

        </View>

    );
}
const styles = StyleSheet.create({
    container: {
