import axios from "axios";
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
    View, Text, TextInput, TouchableOpacity, StyleSheet,
    SafeAreaView, Keyboard, FlatList
} from "react-native";

import Listagem from "../func/Listagem_pet";

export default function Listar_pets() {
    const [lista, setLista] = useState([]);

    //quando a tela for carregada ele chama essa função
    useEffect(() => {
        listarDados();
    }, []);

    async function deletar(id_) {
        try {
            const res = await axios.get(
                "http://192.168.100.93/api_sgpApp/deletar_pet.php?id=" + id_);
            setLista(res.data.result);
            Keyboard.dismiss();
            alert("Registro deletado");
            listarDados();
        } catch (error) {
            console.log("Erro: ", error);
        }
    }


    async function editar(data) {
        setId(data.id)
        setNome(data.nome)
        setEmail(data.email)
        setSenha(data.senha)
        console.log("clicado ", data);
    }
    //altera os itens
    async function alterarDados() {
        try {
            const res = await axios.get(
                "http://localhost/apireact/alterar.php?nome=" +
                nome +
                "&email=" +
                email +
                "&senha=" +
                senha +
                "&id=" +
                id
            );
            console.log(res.data.result);
            setLista(res.data.result);
            Keyboard.dismiss();
            alert("Registro alterado");
            limpar();
            listarDados();
        } catch (error) {
            console.log("Erro: ", error);
        }
    }

    async function listarDados() {
        try {
            //onde está "192.168.100.93" cooque seu ipv4
            const res = await axios.get("http://192.168.100.93/api_sgpApp/listar_pet.php");
            setLista(res.data.result);
            Keyboard.dismiss();
        } catch (error) {
            console.log('Erro: ', error);
        }
    }
    return (
        <SafeAreaView style={styles.container}>
            <StatusBar hidden={true}></StatusBar>

            <View style={styles.titulo}>

                <Text style={styles.txtTitulo}>Visualizar Pets</Text>

            </View>

            <View style={styles.areaBtn}>
                <TouchableOpacity
                    style={[styles.botao, { backgroundColor: "#1d75cd" }]}
                    onPress={alterarDados}
                >
                    <Text style={styles.botaoText}>Alterar</Text>
                </TouchableOpacity>
            </View>

            <FlatList
                keyExtractor={(item) => item.id}
                data={lista}
                renderItem={({ item }) => <Listagem data={item}
                    deleteItem={deletar}
                    editItem={editar} />}
            />
        </SafeAreaView>
    );
}
//css
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#4F7063",
        opacity: 0.8,
        justifyContent: 'center',
    },
    titulo: {
        justifyContent: 'flex-start',
        width: '100%',
    },
    txtTitulo: {
        backgroundColor: "#4F7063",
        fontSize: 50,
    },
});
