import React from 'react';
import {View, Text, TouchableOpacity, StyleSheet, Image} from "react-native";
import { StatusBar } from "expo-status-bar";

export default function Gerenciar_doac({navigation}){
return (
    <View style={styles.container}>
      <StatusBar hidden={true}></StatusBar>

        <Text style={styles.titulo}>Gerenciar Doações</Text>

        <View style={styles.areaBtn}>
          
          <TouchableOpacity
            style={[styles.botao, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Cadastrar Doações')}
          >
            <Image
            source={require('../../assets/img_cadProt.png')}
            style={styles.image}
            />
            <Text style={styles.botaoText}>Cadastrar{"\n"}Doações</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.areaBtn}>
          
          <TouchableOpacity
            style={[styles.botao, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Listar Doações')}
          >
            <Image
            source={require('../../assets/lupa.png')}
            style={styles.image}
            />
            <Text style={styles.botaoText}>Visualizar{"\n"}Doações</Text>
          </TouchableOpacity>
        </View>


        <View style={styles.tabNav}>

          <View style={{backgroundColor:'#000', height:'80%', width:2, borderRadius:10, marginLeft:'2%'}}/>

         <View style={styles.areaBtnTab}> 
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Gerenciar Protetores')}
          >
            <Image
            source={require('../../assets/user.jpg')}
            style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Protetetores</Text>
          </TouchableOpacity>
        </View>

          <View style={{backgroundColor:'#000', height:'80%', width:2, borderRadius:10}}/>

         <View style={styles.areaBtnTab}> 
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Home')}
          >
            <Image
            source={require('../../assets/img_Home.png')}
            style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Home</Text>
          </TouchableOpacity>
        </View>

          <View style={{backgroundColor:'#000', height:'80%', width:2, borderRadius:10}}/>

          <View style={styles.areaBtnTab}> 
          <TouchableOpacity
            style={[styles.botaoTab, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Gerenciar Protetores')}
          >
            <Image
            source={require('../../assets/user.jpg')}
            style={styles.imageTab}
            />
            <Text style={styles.botaoTextTab}>Protetetores</Text>
          </TouchableOpacity>
        </View>

          <View style={{backgroundColor:'#000', height:'80%', width:2,borderRadius:10, marginRight:'2%'}}/>

        </View>
    </View>
)
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: "space-between",

    },
    titulo:{
      backgroundColor: "#4F7063",
      fontSize: 50,
      height: '19%',
    },
    areaBtn: {
      alignItems: "center",
      justifyContent: "center",
    },
    botao: {
      height: 127,
      padding: 15,
      borderRadius: 30,
      width:'80%',
      flexDirection: "row",
      justifyContent: 'space-between',
      alignItems: "center",
    },
    botaoText: {
      textAlign: 'left',
      fontSize: 27,
      color: "#000",
      fontWeight: "bold",
    },
    image: {
      width:90,
      height:90,
    },
    tabNav: {
      flexDirection: "row",
      justifyContent: 'space-between',
      alignItems: "center",
      backgroundColor: "#4F7063",
      height: '11%',
      width:'100%',
    },
    areaBtnTab:{
      width: '23%',
      height: '10%',
      justifyContent: 'center',
      alignItems: "center",
    },
    botaoTab: {
      justifyContent: 'center',
      alignItems: "center",
    },
    imageTab: {
      width:40,
      height:40,
    },
    botaoTextTab: {
      fontSize: 17,
      color: "#000",
    },
  });
