import axios from "axios";
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
    View, Text, TextInput, TouchableOpacity, StyleSheet,
    SafeAreaView, Keyboard, FlatList
} from "react-native";
import Listagem from "../func/Listagem_doacao";
export default function Listar_doacoes() {
    const [lista, setLista] = useState([]);
    
    //quando a tela for carregada ele chama essa função
    useEffect(() => {
        listarDados();
    }, []);
    async function listarDados() {
        try {
            //onde está "192.168.100.93" cooque seu ipv4
            const res = await axios.get("http://192.168.100.93/api_sgpApp/listar_doacoes.php");
            setLista(res.data.result);
            Keyboard.dismiss();
        } catch (error) {
            console.log('Erro: ', error);
        }
    }
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.areaBtn}>
                <TouchableOpacity
                    style={[styles.botao, { backgroundColor: "#1d75cd" }]}
                    onPress={listarDados}
                >
                    <Text style={styles.botaoText}>Buscar</Text>
                </TouchableOpacity>
            </View>
            <FlatList
                keyExtractor={item => item.id}
                data={lista}
                renderItem={({ item }) => (<Listagem data={item} />)}
            />
        </SafeAreaView>
    );
}
//css
const styles = StyleSheet.create({
    container: {
        flex: 1,
        
    },
    text: {
        marginTop: 25,
        marginBottom: 15,
        fontSize: 25,
        fontWeight: "bold",
    },
    input: {
        backgroundColor: "#FFF",
        borderWidth: 1,
        borderColor: "#DDD",
        borderRadius: 5,
        width: "90%",
        padding: 10,
        fontSize: 18,
    },
    areaBtn: {
        alignItems: "center",
        flexDirection: "row",
        marginTop: 15,
        justifyContent: "space-around",
    },
    botao: {
        height: 50,
        justifyContent: "center",
        alignItems: "center",
        padding: 15,
        borderRadius: 5,
    },
    botaoText: {
        fontSize: 22,
        color: "#FFF",
    },
    resultado: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    itemText: {
        fontSize: 22,
    },
});
