import React from 'react';
import {View, Text, TouchableOpacity, StyleSheet, Image} from "react-native";

import {NavigationContainer} from '@react-navigation/native';
import Routes from '../routes';

export default function Home({navigation}){
return (
    <View style={styles.container}>
        <Text style={styles.text}>Bem Vindo!</Text>

        <View style={styles.areaBtn}>
          
          <TouchableOpacity
            style={[styles.botao, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Gerenciar Pets')}
          >
            <Image
            source={require('../../assets/pata.png')}
            style={styles.image}
            />
            <Text style={styles.botaoText}>Gerenciar{"\n"}Pets</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.areaBtn}>
          
          <TouchableOpacity
            style={[styles.botao, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Cadastrar Protetores')}
          >
            <Image
            source={require('../../assets/user.jpg')}
            style={styles.image}
            />
            <Text style={styles.botaoText}>Gerenciar{"\n"}Protetores</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.areaBtn}>
          <TouchableOpacity
            style={[styles.botao, { backgroundColor: "#4F7063" }]}
            onPress={ () => navigation.navigate('Cadastrar Pet')}
          >
            <Image
            source={require('../../assets/doacao.png')}
            style={styles.image}
            />
            <Text style={styles.botaoText}>Gerenciar{"\n"}Doações</Text>
          </TouchableOpacity>
        </View>

        <View style={{backgroundColor: "#4F7063", height: '10%', width:'100%'}}></View>

        <NavigationContainer>
          <Routes/>
        </NavigationContainer>

    </View>
)
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: "space-between",

    },
    text:{
      backgroundColor: "#4F7063",
      fontSize: 50,
      height: '15%',
      textAlignVertical:'center',
    },
    areaBtn: {
      alignItems: "center",
      justifyContent: "center",
    },
    botao: {
      height: 127,
      padding: 15,
      borderRadius: 30,
      width:'80%',
      flexDirection: "row",
      justifyContent: 'space-between',
      alignItems: "center",
    },
    botaoText: {
      textAlign: 'left',
      fontSize: 27,
      color: "#000",
      fontWeight: "bold",
    },
    image: {
      width:90,
      height:90,
    }
  });
